import React from "react";
import { Formik, Form, Field, FieldArray } from "formik";

// Here is an example of a form with an editable list.
// Next to each input are buttons for insert and remove.
// If the list is empty, there is a button to add an item.
const Speakers = () => (
  <div>
    <h1>Speakers</h1>
    <Formik
      initialValues={{ speakers: [], commenters: [] }}
      onSubmit={(values) =>
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
        }, 500)
      }
      render={({ values }) => (
        <Form>
          <FieldArray
            name="speakers"
            render={(arrayHelpers) => (
              <div>
                {values.speakers && values.speakers.length > 0 ? (
                  values.speakers.map((speakers, index) => (
                    <div key={index}>
                      <Field name={`speakers.${index}`} />
                      <button
                        type="button"
                        onClick={() => arrayHelpers.remove(index)} 
                      >
                        -
                      </button>
                      <button
                        type="button"
                        onClick={() => arrayHelpers.insert(index, "")} // insert an empty string at a position
                      >
                        +
                      </button>
                    </div>
                  ))
                ) : (
                  <button type="button" onClick={() => arrayHelpers.push("")}>
                    {/* show this when user has removed all friends from the list */}
                    Add a speaker
                  </button>
                )}

              </div>
            )}
          />
          <h1>Commenters</h1>
          <FieldArray
            name="commenters"
            render={(arrayHelpers) => (
              <div>
                {values.commenters && values.commenters.length > 0 ? (
                  values.commenters.map((commenters, index) => (
                    <div key={index}>
                      <Field name={`commenters.${index}`} />
                      <button
                        type="button"
                        onClick={() => arrayHelpers.remove(index)} 
                      >
                        -
                      </button>
                      <button
                        type="button"
                        onClick={() => arrayHelpers.insert(index, "")} // insert an empty string at a position
                      >
                        +
                      </button>
                    </div>
                  ))
                ) : (
                  <button type="button" onClick={() => arrayHelpers.push("")}>
                    {/* show this when user has removed all friends from the list */}
                    Add a speaker
                  </button>
                )}

              </div>
            )}
          />
          <div>
                  <button type="submit">Submit</button>
          </div>
        </Form>
      )}
    />
  </div>
);

export default Speakers;
